import"./darkmode-CXfVOGe4.js";const a=document.getElementById("login-form"),e=document.getElementById("error-message");a.addEventListener("submit",async o=>{o.preventDefault(),e.textContent="",e.style.display="none";const n=document.getElementById("username").value.trim(),r=document.getElementById("password").value;try{const t=await fetch("/auth/login",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({username:n,password:r})});if(t.ok){const s=await t.json();localStorage.setItem("token",s.token),window.location.href="./index.html"}else e.textContent="Invalid username or password",e.style.display="block"}catch(t){console.error("Login error:",t),e.textContent="An error occurred. Please try again.",e.style.display="block"}});
